import { create, ApisauceInstance } from 'apisauce';
import { Item } from '../model/item';
import { User } from '../model/user';

// const BASE_URL: string = "http://example-ecommerce.herokuapp.com/";
const BASE_URL: string = "http://127.0.0.1:3002/";

const Authorization: string = "Authorization";

class ServiceAPI {

    private readonly apiAuth: ApisauceInstance = create({
        baseURL: BASE_URL,
        headers: {
            Accept: 'application/json',
        },
    });

    public async getProductList() {
        return await this.apiAuth.get('products');
    }

    public async login(user: User) {
        return await this.apiAuth.post('users/login', user);
    }

    public async addCustomer(body: any) {
        return await this.apiAuth.post('users/customer', body);
    }

    public async getSalesOrderList(token: string) {
        this.apiAuth.setHeader(Authorization, "Bearer "+ token);
        return await this.apiAuth.get('sales/order');
    }

    public async invoice(items: Item[], token: string) {
        this.apiAuth.setHeader(Authorization, "Bearer "+ token);
        return await this.apiAuth.post('sales/order', items);
    }

}

export default new ServiceAPI();
